    private void liberaLogin(boolean condicao){
        //1Painel
        jButtonAluno.setEnabled(condicao);
        jButtonSecretaria.setEnabled(condicao);
        jButtonRH.setEnabled(condicao);
        jButtonProfessor.setEnabled(condicao);
        jButtonCoordenador.setEnabled(condicao);
        //2Painel
        jButtonAvanca.setEnabled(!condicao);
        jButtonVoltar.setEnabled(!condicao);
        jTextFieldLogin.setEditable(!condicao);
        jPasswordFieldSenha.setEditable(!condicao);
        //Apagar texto
        jTextFieldLogin.setText("");
        jPasswordFieldSenha.setText("");
    }
