    private void secretaria(){
        try{
            if(!(jTextFieldLogin.getText().toLowerCase().equals("secretaria"))) throw new Exception("Login Incorreto");
            String valor = new String(jPasswordFieldSenha.getPassword());
            if(!(valor.toLowerCase().equals("secretaria"))) throw new Exception("Senha Incorreta");
            TelaDaSecretaria tela = new TelaDaSecretaria();
            tela.setLocationRelativeTo(rootPane);
            tela.setVisible(true);
            dispose();
        }catch(Exception e){
            JOptionPane.showMessageDialog(rootPane, e.getMessage(), "ERRO", JOptionPane.ERROR_MESSAGE);
            mensagemErro3();
        }
    }
