    private void selecionaLogin(){
        switch(butao){
            case "secretaria":
                secretaria();
                break;
            case "rh":
                rh();
                break;
            case "aluno":
                aluno();
                break;
            case "preofessor":
                professor();
                break;
            default:
               coordenador();
        }
    }

